    private void FtoCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FtoCActionPerformed
       // get the text from the first box
        
        numBox1.getText();
       
       // create a string to store the Text given from the number box
       
        String FtoC = numBox1.getText();
       
       // create a parse integer to convert the string to numbers
       
       double FC = Double.parseDouble(FtoC);
       
       // tell the double to convert fahrenhiet to celcuis
       
       double f = (FC-32) / 1.8  ;
       
       
       // show the answer on the second box
       
       numBox2.setText("" +f);
       
       
    }//GEN-LAST:event_FtoCActionPerformed

