    private void CtoFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CtoFActionPerformed
        // get text from number box 2
        
        numBox2.getText();
        
        // create a string to store the numbers given
        
        String CtoF = numBox2.getText();
        
        // make a parse double to convert the string to numbers
        
        double CF = Double.parseDouble(CtoF);
        
        // convert celcuis to fahrenhiet
        
        double C = (CF*1.8)+32;
        
        //show the answer on the fahrenhiet box
        
        numBox1.setText( ""+ C);
    }//GEN-LAST:event_CtoFActionPerformed

